mapper.update:
update tbl_user set
upass=#{upass}, umail=#{umail}, uname=#{uname},tel=#{tel}, address=#{address}
where
uid=#{uid}

update tbl_user set
umail=#{umail}, uname=#{uname},tel=#{tel}, address=#{address}
where
uid=#{uid}

mapper.select:
select uid,uname,umail,tel,address from tbl_user
where uid like concat('%',#{keyword},'%') order by uid desc limit #{pageStart},#{perPageNum}
where uname like concat('%',#{keyword},'%') order by uname desc limit #{pageStart},#{perPageNum}
